  public addDefs(context) {
    context.append('defs');
    this.createPistolDef(context);
    this.createWaterPistolDef(context);
    this.createKnifeDef(context);
    this.createVehicleDef(context);
    this.createFistDef(context);
    this.createQuestionMarkDef(context);
    this.createMagicWandDef(context);
    this.createMaleDef(context);
    this.createFemaleDef(context);

    const genderCircleDef = context
      .select('defs')
      // .append('defs')
      .append('g')
      .attr('id', 'genderCircle')
      .append('circle')
      .attr('r', 30);

    const raceCircleDef = context
      .select('defs')
      // .append('defs')
      .append('g')
      .attr('id', 'raceCircle')
      .append('circle')
      .attr('r', 12);

    const murderCircleDef = context
      .select('defs')
      // .append('defs')
      .append('g')
      .attr('id', 'murderCircle')
      .append('circle')
      .attr('r', 3);
  }

